/* Code tidied up by ScrapBook */
.mw-content-ltr ul, .mw-content-rtl .mw-content-ltr ul { margin: 0.3em 0px 1em 1.6em; }
div#mw-content-text a.external[href^="http://bugzilla.mozilla.org"], div#mw-content-text a.external[href^="https://bugzilla.mozilla.org"], div#mw-content-text a.external[href^="http://bugzil.la"], div#mw-content-text a.external[href^="https://bugzil.la"] { background: rgba(0, 0, 0, 0) url("data:image/png;base64,") no-repeat scroll right center / 14px auto; padding-right: 16px; }
div#mw-content-text a.external[href^="http://wiki.mozilla.org"], div#mw-content-text a.external[href^="https://wiki.mozilla.org"] { background: rgba(0, 0, 0, 0) url("data:image/png;base64,") no-repeat scroll right center / 10px auto; padding-right: 14px; }
div#bodyContent { font-size: 0.9em; }
h1#firstHeading, div#bodyContent h1, div#bodyContent h2 { font-weight: normal; }
div#bodyContent h3, div#bodyContent h4, div#bodyContent h5, div#bodyContent h6 { font-weight: bold; }
h1#firstHeading, div#bodyContent h1 { font-size: 200%; margin-top: 1em; }
div#bodyContent h2 { font-size: 150%; }
div#bodyContent h3 { font-size: 140%; }
div#bodyContent h4 { font-size: 130%; }
